import './App.css';
import React, { useState } from 'react';

function App() {

  const [ value, setValue ] = useState('');

  const handleChange = (e) => {
    setValue(e.target.value);
  }

  const showValue = () => {
    console.log(value);
  }

  return (
    <div className="app">
      <input type="number" 
      value={value} 
      onChange={handleChange} />
      <button 
      onClick={showValue}>+</button>
    </div>
  );
}

export default App;
