import './App.css';
import React, { useState } from 'react';

function App() {

    const [ value, setValue ] = useState('');

    const handleChange = (e) => {
        setValue(e.target.value);
    }

    const showValue = () => {
        return value;
    }

    return (
        <div className="app">
            <h1>{ value }</h1>
            <input type="number" 
            value={value} 
            onChange={handleChange} />
            <button 
            onClick={showValue}>+</button>
        </div>
    );
}

export default App;
