import './App.css';
import React, { useState, useEffect } from 'react';

function App() {

    const [ count, setCount ] = useState(0);

    useEffect(() => {
        document.p = `total expense: ${count} times`;
    });

    const handleChange = (e) => {
        setCount(e.target.value)
    }

    const showValue = () => {
        return count
    }

    return (
        <div className="app">
            <input type="number" 
            value={count} 
            onChange={handleChange} />
            <button 
            onClick={showValue}>+</button>
        </div>
    );
}

export default App;
