import './App.css';
import React, { useState, useRef } from 'react';

function App() {

    let totalExpense = 0;
    const [ count, setCount ] = useState(0);
    const printCount = useRef(null);
    const expense = parseInt(count, 10);

    const handleChange = (e) => {
        setCount(e.target.value);
    }

    const showValue = ({totalExpense}) => {
        printCount.current.textContent = count + totalExpense;
    }

    console.log(expense)


    return (
        <div className="app">
            <h1 ref={printCount}>0</h1>
            <input type="text" 
            value={count} 
            onChange={handleChange} />
            <button 
            onClick={showValue}>+</button>
        </div>
    );
}

export default App;
