import './App.css';
import React, { useState, useRef } from 'react';

function App() {

    const [ count, setCount ] = useState(0);
    const printCount = useRef(null);

    const handleChange = (e) => {
        setCount(e.target.value);
    }

    const showValue = () => {
        return printCount.current.textContent = count;
    }

    return (
        <div className="app">
            <h3 ref={printCount}>
            Total Expense: </h3>
            <input type="number" 
            value={count} 
            onChange={handleChange} />
            <button 
            onClick={showValue}>+</button>
        </div>
    );
}

export default App;
